export const getDateMonth = (date) => {
  const d = new Date(date);
  const month = d.getMonth();

  if (month < 10) {
    return `0${month}`;
  }
  return month;
};

export const getDateYear = (date) => {
  const d = new Date(date);
  const year = d.getFullYear();
  return year;
};
