import React, { useEffect, useState } from "react";

import Loading from "../../components/Loading/Loading.jsx";
import Error from "../../components/Errors/Error.jsx";

import Banner from "../Reportes/Sections/Banner/Banner.jsx";

import process from "../../../tools/config.tools.json";

export default function Entrada({ id, route }) {
  const [isLoading, setIsLoading] = useState(true);
  const [data, setData] = useState(null);

  useEffect(() => {
    fetch(`${process.DJANGO_REST_API}/${route}/${id}`)
      .then((res) => res.json())
      .then((result) => {
        setData(result);
        setIsLoading(false);
      })
      .catch((err) => console.log(err))
      .finally(() => setIsLoading(false));
  }, []);

  if (isLoading) return <Loading />;

  if (!data) {
    return <Error title={"hola"} />;
  }

  if (data.detail) {
    return <Error />;
  }

  console.log(data);
  /*
  id
  title
  description
  content
  url
  thumbnail
  status
  createdAt
  */

  return (
    <>
      <Banner img={data.thumbnail} title={data.title} text={data.description} />
      <main className="details-content">
        Lorem ipsum, dolor sit amet consectetur adipisicing elit. Amet sapiente
        in nihil quaerat harum pariatur aperiam quasi error quos. Quia, alias!
        Quisquam possimus placeat accusamus consequuntur repellat ratione,
        delectus magni.
      </main>
    </>
  );
}
